
public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n1 = 10;
		int n2 = 5;
		int resultado;
		
		//Suma
		System.out.println("resultado n1 + n2 = " + (resultado = n1 + n2));
		//Resta
		System.out.println("resultado n1 - n2 = " + (resultado = n1 - n2));
		//Multiplicacion
		System.out.println("resultado n1 * n2 = " + (resultado = n1 * n2));
		//Division
		System.out.println("resultado n1 / n2 = " + (resultado = n1 / n2));
		//Modulo
		System.out.println("resultado n1 % n2 = " + (resultado = n1 % n2));

	}

}
